package Hello.java;

public class Hello008 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10;
		
		a++;
		System.out.println("a :" + a);
		System.out.println("a++ :" + a++);
		
		System.out.println("a :" + a);
		System.out.println("++a :" + ++a);
		
		a--;
		System.out.println("a :" + a);
		
		a+=5;
		System.out.println("a :" + a);
		
		a-=5;
		System.out.println("a :" + a);
		
		a*=5;
		System.out.println("a :" + a);
		
		a/=5;
		System.out.println("a :" + a);
		
		a%=5;
		System.out.println("a :" + a);
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
