package Hello.java;


public class Hello010 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 200;

		if (a < 100) {
			System.out.println("100보다 작군요");
			System.out.println("참이면 보이죠");

		} else {
			System.out.println("100보다 크군요");
			System.out.println("거짓이면 보이겠죠");

		}
	}

}
