package Hello.java;

public class Hello011 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 75;

		if (a > 50) {
			if (a < 100) {
				System.out.println(a + "는 50보다 크고, 100보단 작군요");
			} else {
				System.out.println(a + "는 100보다 크군요");
			}
		} else {
			System.out.println(a + "50보단 작군요");
		}

	}

}
