package Hello.java;

public class Hello014 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		
		for (i = 0; i < 3; i++)
		{
			System.out.println("반복을 위한 i 값:" + i);
			System.out.println("또 안녕하세요.");
		}
		
		System.out.println();
		System.out.println();
		
		for (i = 1; i <= 3; i++) 
		{
			System.out.println("반복 횟수" + i);
		}
		

	}

}
